CREATE TABLE ROLE (ID BIGINT NOT NULL, CREATE_TS TIMESTAMP, CREATED_BY VARCHAR(50), DELETE_TS TIMESTAMP, DELETED_BY VARCHAR(50), NAME VARCHAR(255), UPDATE_TS TIMESTAMP, UPDATED_BY VARCHAR(50), VERSION INTEGER NOT NULL, PRIMARY KEY (ID))
CREATE TABLE USER_ (ID BIGINT NOT NULL, CREATE_TS TIMESTAMP, CREATED_BY VARCHAR(50), DELETE_TS TIMESTAMP, DELETED_BY VARCHAR(50), LOGIN VARCHAR(50) NOT NULL, UPDATE_TS TIMESTAMP, UPDATED_BY VARCHAR(50), VERSION INTEGER NOT NULL, PRIMARY KEY (ID))
CREATE TABLE SESSION (ID BIGINT NOT NULL, ACTIVE BOOLEAN NOT NULL, CREATE_TS TIMESTAMP, CREATED_BY VARCHAR(50), DELETE_TS TIMESTAMP, DELETED_BY VARCHAR(50), SESSION_ID UUID, UPDATE_TS TIMESTAMP, UPDATED_BY VARCHAR(50), VERSION INTEGER NOT NULL, USER_ID BIGINT, PRIMARY KEY (ID))
CREATE UNIQUE INDEX SESSION_ID_IDX ON SESSION (SESSION_ID)
CREATE TABLE USER_ROLES (User_ID BIGINT NOT NULL, roles_ID BIGINT NOT NULL, PRIMARY KEY (User_ID, roles_ID))
ALTER TABLE SESSION ADD CONSTRAINT FK_SESSION_USER_ID FOREIGN KEY (USER_ID) REFERENCES USER_ (ID)
ALTER TABLE USER_ROLES ADD CONSTRAINT FK_USER_ROLES_User_ID FOREIGN KEY (User_ID) REFERENCES USER_ (ID)
ALTER TABLE USER_ROLES ADD CONSTRAINT FK_USER_ROLES_roles_ID FOREIGN KEY (roles_ID) REFERENCES ROLE (ID)
